import React, { useState } from 'react'

function CategoryComponent(props) {
  const [name, setName] = useState('')
  const [categoryType, setCategoryType] = useState('')
  const [imageUrl, setImageUrl] = useState('')
  const [description, setDescription] = useState('')
  const [sIdList, setSubCategoryIdList] = useState('')

  const handleCancelClick = () => {
    props.showComponent(false)
  }
  const handleAddModule = async () => {
    let subCategoryIdList = sIdList.split(',')
    const moduleData = {
      name: name,
      categoryType: categoryType,
      imageUrl: imageUrl,
      description: description,
      subCategoryIdList: subCategoryIdList,
    }
    try {
      const response = await fetch('http://3.13.92.74:30005/questionnaire/admin/category', {
        method: 'POST',
        headers: {
          'X-User-ID': '1',
          'Content-Type': 'application/json',
        },
        body: JSON.stringify(moduleData),
      })
      if (response.ok) {
        console.log('File uploaded successfully')
        props.showComponent(false)
      } else {
        console.error('Error uploading file')
      }
    } catch (error) {
      console.error(error)
    }
  }

  return (
    <>
      <div className="flex justify-between mt-6">
        <div style={{ width: '80%' }}>
          <h2 className="text-lg font-medium">
            <span className="font-bold"> Name:</span>{' '}
            <p className="border border-gray-400 p-1 rounded-sm">
              <input value={name} onChange={(e) => setName(e.target.value)} />
            </p>
          </h2>
          <h2 className="text-lg font-medium">
            <span className="font-bold">Category Type:</span>{' '}
            <p className="border border-gray-400 p-1 rounded-sm">
              <input value={categoryType} onChange={(e) => setCategoryType(e.target.value)} />
            </p>
          </h2>
          <h2 className="text-lg font-medium">
            <span className="font-bold">ImageUrl:</span>{' '}
            <p className="border border-gray-400 p-1 rounded-sm">
              <input value={imageUrl} onChange={(e) => setImageUrl(e.target.value)} />
            </p>
          </h2>
          <h2 className="text-lg font-medium">
            <span className="font-bold">Description:</span>{' '}
            <p className="border border-gray-400 p-1 rounded-sm">
              <input value={description} onChange={(e) => setDescription(e.target.value)} />
            </p>
          </h2>
          <h2 className="text-lg font-medium">
            <span className="font-bold">SubCategoryId List:</span>{' '}
            <p className="border border-gray-400 p-1 rounded-sm">
              <input value={sIdList} onChange={(e) => setSubCategoryIdList(e.target.value)} />
            </p>
          </h2>
        </div>
        <div
          style={{
            width: '10%',
            display: 'flex',
            flexDirection: 'column',
            justifyContent: 'center',
          }}
          className="flex flex-col mt-4"
        >
          <a>
            <img
              src="https://cdn-icons-png.flaticon.com/512/992/992482.png"
              alt="Create"
              className="cursor-pointer mb-4"
              onClick={handleAddModule}
              style={{ width: 50, height: 50 }}
            />
          </a>
          <a>
            <img
              src="data:image/png;base64,"
              alt="Cancel"
              onClick={handleCancelClick}
              className="cursor-pointer mb-4"
              style={{ width: 50, height: 50 }}
            />
          </a>
        </div>
      </div>
    </>
  )
}
export default CategoryComponent
